exports.id = {
    'class': 'itemClass',
    inventorySlot: 'inventoryType',
    item: 'id',
    quality: 'quality',
    subclass: 'itemSubClass',
};

exports.text = {
    'class': function(text) {
        return ['itemClassText', text];
    },

    gearScore: function(text) {
        return ['gearScore', parseFloat(text)];
    },

    icon: function(text) {
        return ['icon', text.toLowerCase()];
    },

    inventorySlot: function(text) {
        return ['inventoryTypeText', text];
    },

    level: function(text) {
        return ['itemLevel', parseInt(text, 10)];
    },

    link: function(text) {
        return ['link', text];
    },

    name: function(text) {
        return ['name', text];
    },

    quality: function(text) {
        return ['qualityText', text];
    },

    subclass: function(text) {
        return ['itemSubClassText', text];
    }
};

exports.json = {
    armor: 'armor',
    displayId: 'displayid',
    heroic: 'heroic',
    itemSet: 'itemset',
    maxDurability: 'dura',
    requiredLevel: 'reqlevel',
    sellPrice: 'sellprice'
};
