var test = require('tap').test,
    wowhead = require('../');

test('item by id', function(t) {
    wowhead(27903, function(err, item) {
        t.error(err);
        t.ok(item);
        t.equal(item.id, 27903);
        t.equal(item.name, 'Sonic Spear');
        t.end();
    });
});

test('item by name', function(t) {
    wowhead('Breeching Comet', function(err, item) {
        t.error(err);
        t.ok(item);
        t.equal(item.id, 34616);
        t.equal(item.name, 'Breeching Comet');
        t.end();
    });
});
