var test = require('tap').test,
    armory = require('armory').defaults({ region: 'us', fallback: false }),
    wowhead = require('../');

test('basic properties', function(t) {
    wowhead(32575, function(err, item) {
        t.error(err);
        t.ok(item);

        armory.item(32575, function(err, armoryItem) {
            t.error(err);
            t.ok(armoryItem);

            for (var prop in armoryItem) {
                if (item[prop] !== undefined) {
                    t.equal(item[prop], armoryItem[prop], prop);
                }
            }

            t.end();
        });
    });
});